const lookback = $('Form intake').first().json['Loopback period']
const cutoff = new Date(Date.now() - lookback * 24 * 60 * 60 * 1000);

return items.filter(item => {
  const pub = new Date(item.json.isoDate || item.json.pubDate);
  return pub >= cutoff;
});

// Detect HTML in multiple possible fields
const html = $json.data 

// If not a string, return empty clean text
if (typeof html !== "string") {
  return {
    cleaned_text: ""
  };
}

// remove script & style blocks completely
let text = html
  .replace(/<script[\s\S]*?<\/script>/gi, '')
  .replace(/<style[\s\S]*?<\/style>/gi, '');

// remove HTML comments
text = text.replace(/<!--[\s\S]*?-->/g, '');

// remove all HTML tags
text = text.replace(/<\/?[^>]+(>|$)/g, ' ');

// collapse whitespace
text = text.replace(/\s+/g, ' ').trim();

// Return a **single object**, NOT an array
return {
  cleaned_text: text
};

